import styled from 'styled-components';

export const HeaderArea = styled.div `

    .texto--header{

        p{
            font-size:32px;
            font-weight: bold;
            text-align: center;
        }

        .sub-title{
            font-size:16px;
            text-align: center;
            margin-bottom:50px;
        }

    }

    .section--services{
        display: grid;
        grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
        grid-gap: 20px;
    }

    .estrutura--metalica {

        background-color: #ccc;
        padding: 20px;
        text-align: center;
        text-decoration:none;

        .estrutura--metalica--tile p {
            font-size:20px;
            font-weight: bold;
        }
      }



`
