import styled from 'styled-components';

export const HeaderArea = styled.div ` 

.menu {
    list-style: none;
    margin: 0;
    padding: 0;
  }

  .menu li {
    display: inline-block;
  }

  .menu li a {
    display: block;
    padding: 10px;
    text-decoration: none;
  }

  .dropdown .submenu {
    display: none;
    position: absolute;
    background-color: #ccc;
    padding: 10px;
  }

  .dropdown:hover .submenu {
    display: block;
  }

  .submenu li {
    display: block;
  }



`
