import styled from 'styled-components';

export const HeaderArea = styled.div `

    .btn{

        background-color:#2F608E;
        padding:10px;
        margin:10px;
        border-radius:10px;
        cursor: pointer;

       

        .btn--text{
            font-size:14px;
            color: #fff;
            padding:5px;
        }

        .btn--text:hover{
            color:#000;
            
        }

       
    }

    .btn:hover {
        background-color: #4D8076;
    }
 

`

