import styled from 'styled-components';

export const HeaderArea = styled.div ` 

/* Estilo para o submenu */
.submenu {
  display: none;
  position: absolute;
  background-color: #333;
  padding: 10px;
}

/* Estilo para o item de menu pai quando hover */
li:hover .submenu {
  display: block;
}

/* Outros estilos para o submenu e links do submenu */
.submenu li {
  margin-right: 0;
}

.submenu a {
  color: #fff;
  text-decoration: none;
}

.submenu a:hover {
  text-decoration: underline;
}



`
