import styled from 'styled-components';

export const HeaderArea = styled.div ` 

    .submenu {
        display: none;
        position: absolute;
        background-color: #333;
        padding: 10px;
    }

    li:hover .submenu {
        display: block;
    }

    .submenu li {
        margin-right: 0;
    }

    .submenu a {
        color: #fff;
        text-decoration: none;
    }

    .submenu a:hover {
        text-decoration: underline;
    }


`
